function Categoria(props: CategoriaProps) {
    return (
        <div className="box-decoration-slice bg-gradient-to-r from-indigo-600 to-pink-500 border-4 border-indigo-500 text-white">
            {props.nombre}

            <div className="p-2">
                {props.descripcion}
                </div>

            <div className="p-2">
                <ul>
                    {props.tareas?.map((tarea, index) => (
                        <li key={index}>{tarea}</li>
                    ))}
                </ul>

                </div>

      </div>
    );
}


interface CategoriaProps {
    nombre: string;
    descripcion?: string;
    tareas?: string[];       // TODO: Cambiar a Tarea[]
    pendientes?: string[];   // TODO: Cambiar a Tarea[]
}

export default Categoria;
